CREATE TABLE IF NOT EXISTS indication_category (
    id SERIAL PRIMARY KEY,
    indication VARCHAR(100) NOT NULL
);

CREATE TYPE stock_status_enum AS ENUM ('CRITICAL', 'NORMAL', 'HIGH');

CREATE TABLE IF NOT EXISTS pharmacy_medicine_stock (
    id SERIAL PRIMARY KEY,
    medicine_code VARCHAR(50) NOT NULL,
    medicine_name VARCHAR(255) NOT NULL,
    quantity INT,
    stock_status stock_status_enum NOT NULL,
    created_at TIMESTAMP NOT NULL,
    indication_category_id INT NOT NULL,
    pharmacy_id INT NOT NULL,

    CONSTRAINT fk_pharmacy FOREIGN KEY (pharmacy_id) REFERENCES pharmacy(id),

    CONSTRAINT fk_indication_category FOREIGN KEY (indication_category_id) REFERENCES indication_category(id)
);

CREATE INDEX idx_medicine_name ON pharmacy_medicine_stock (medicine_name);

CREATE TABLE IF NOT EXISTS pharmacy (
    cnpj VARCHAR(14) PRIMARY KEY UNIQUE,
    state VARCHAR(2) NOT NULL,
    city VARCHAR(50) NOT NULL,
    name VARCHAR(255) NOT NULL,
    address VARCHAR(255) NOT NULL,
    neighborh VARCHAR(255) NOT NULL,
    accreditation_date DATE NOT NULL
);

CREATE INDEX idx_state_city_neighb ON pharmacy (state, city, neighborhood);

